                    fill: new _ol2.default.style.Fill({
                        color: "rgba(0, 183, 152, 0.3)"
                    }),
                    stroke: new _ol2.default.style.Stroke({
                        color: "rgba(0, 183, 152, 0.8)",
                        width: 3
                    }),
