    if (!target) {
        return null;
    }

    if (type === GlobeViewExtended.EVENTS.AFTER_RENDER) {
        target.addFrameRequester(type, callback);
    } else {
        target.addEventListener(type, callback);
    }

    return {
        target: target,
        callback: callback,
        type: type
    };
