        div.title = "Supprimer la couche";
        div.layerId = obj.id;

        var context = this;
        if (div.addEventListener) {
            div.addEventListener("click", function (e) {
                context._onDropLayerClick(e);
            });
        } else if (div.attachEvent) {
            // internet explorer
            div.attachEvent("onclick", function (e) {
                context._onDropLayerClick(e);
