div[id^=GPlayerSwitcher-] {
  font-size: 11px;
}

div[id^=GPlayerSwitcher-] [draggable] {
  -moz-user-select: none;
  -khtml-user-select: none;
  -webkit-user-select: none;
  user-select: none;
  /* Required to make elements draggable in old WebKit */
  -khtml-user-drag: element;
  -webkit-user-drag: element;
}

/* Manage opening of the components through hiden checkboxes */

div[id^=GPlayerSwitcher-] input[type="checkbox"] + div,
div[id^=GPlayerSwitcher-] input[type="checkbox"] + label + div {
  max-height: 0px;
}

div[id^=GPlayerSwitcher-] label {
  display: block;
}

/* Showing/hiding layers list */

label[id^=GPshowLayersListPicto] {
  position: relative;
  float: right;
  display: block;
  opacity: 1;
  transition: opacity 0.5s ease-out 0s, box-shadow  0.5s ease-out 0s, border  0.5s ease-out 0s, border-radius  0.5s ease-out 0s;
  cursor: pointer;
}

span[id^=GPshowLayersListOpen],
span[id^=GPshowLayersListClose] {
  position: absolute;
  display: block;
  transition: border-radius 0.5s ease-out 0s, opacity 0.5s ease-out 0s;
}

span[id^=GPshowLayersListOpen] {
  background-position: 0 0;
  opacity: 1;
}

span[id^=GPshowLayersListClose] {
  opacity: 0;
}

div[id^=GPlayerSwitcher-] input[type="checkbox"]:checked + div + label[id^=GPshowLayersListPicto] {
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}

div[id^=GPlayerSwitcher-] input[type="checkbox"]:checked + div + label[id^=GPshowLayersListPicto] span[id^=GPshowLayersListClose] {
  opacity: 1;
}

/* Layers list */

div[id^=GPlayersList] {
  position: relative;
  width: 180px;
  border-bottom-right-radius: 0;
  opacity: 0;
  overflow: auto;
  transition: max-height 0.5s ease-out 0s, opacity 0.5s ease-out 0.25s;
}

div[id^=GPlayerSwitcher-] input[type="checkbox"]:checked + div[id^=GPlayersList] {
  max-height: 232px;
  opacity: 1;
  transition: max-height 0.5s ease-in 0s, opacity 0.25s ease-in 0s;
}

/* Layer : general */

.GPlayerSwitcher_layer {
  position: relative;
  top: 0;
  background-color: #FFF;
  border-bottom: 1px dotted #CCC;
  overflow: hidden;
}

div[id^=GPlayersList] .GPlayerSwitcher_layer:last-child {
  border-bottom: none;
}

.GPlayerBasicTools,
.GPlayerAdvancedTools {
  position: relative;
  width: 100%;
  height: 28px;
}

.GPlayerVisibility,
.GPlayerInfo,
.GPlayerInfoOpened,
.GPlayerRemove {
  width: 28px;
  height: 28px;
  cursor: pointer;
}

.GPlayerVisibility,
.GPlayerName,
.GPlayerInfo,
.GPlayerInfoOpened,
.GPlayerRemove,
.GPlayerOpacity,
.GPlayerOpacityValue {
  position: absolute;
  top: 0;
}

.GPghostLayer {
  opacity: 0;
}

/* Layer basic tools */

.GPlayerBasicTools {
  background-color: #FFF;
}

.GPlayerVisibility {
  left: 0;
  background-position: -28px 0;
}

input[type="checkbox"]:checked + .GPlayerVisibility {
  background-position: 0 0;
}

.GPlayerName {
  left: 28px;
  width: calc(100% - 56px);
  line-height: 28px;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  padding-left: 4px;
  cursor: move;
}

.outOfRange .GPlayerName {
  color: #AAA;
}

/* Showing layer advanced tools */

.GPshowLayerAdvancedTools {
  top: 0;
  right: 0;
  background-position: -112px 0;
}

.GPlayerAdvancedTools {
  display: block;
  max-height: 0;
  opacity: 0;
  transition: max-height 0.5s ease-out 0s, opacity 0.5s ease-out 0s;
}

div[id^=GPlayerSwitcher-] input[type="checkbox"]:checked + label + .GPlayerAdvancedTools {
  max-height: 28px;
  opacity: 1;
}

/* Layer advanced tools */

.GPlayerInfo {
  left: 0;
  background-position: -55px 0;
}

.GPlayerInfoOpened {
  left: 0;
  background-position: -83px 0;
}

.GPlayerOpacity {
  left: 28px;
  width: calc(100% - 100px);
  height: 28px;
  padding-left: 8px;
}

.GPlayerOpacityValue {
  width: 32px;
  left: calc(100% - 60px);
  line-height: 28px;
  font-size: 10px;
  font-style: italic;
  cursor: default;
}

.GPlayerRemove {
  right: 0;
  background-position: -140px 0;
}

/* Opacity slider : general */

.GPlayerOpacity input {
  -webkit-appearance: none;
  -moz-appearance: none;
  background: none;
  display: block;
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
  overflow: hidden;
  cursor: pointer;
}

.GPlayerOpacity input[type="range"]:focus {
  box-shadow: none;
  outline: none;
}

/* Opacity slider : Chrome, Safari, Opera */

.GPlayerOpacity input[type="range"]::-webkit-slider-runnable-track {
  -webkit-appearance: none;
  height: 3px;
}

.GPlayerOpacity input[type="range"]::-webkit-slider-thumb:before {
  position: absolute;
  top: 0;
  right: 50%;
  left: -200px;
  height: 3px;
  pointer-events: none;
}

.GPlayerOpacity input[type="range"]::-webkit-slider-thumb {
  width: 9px;
  height: 17px;
  -webkit-appearance: none;
  border: 2px solid #FFF;
  background-color: #505050;
  position: relative;
  top: -7px;
}

/* Opacity slider : Firefox */

.GPlayerOpacity input[type="range"]::-moz-range-track {
  width: 80px;
  height: 3px;
}

.GPlayerOpacity input[type="range"]::-moz-range-thumb {
  width: 5px;
  height: 13px;
  border: 2px solid #FFF;
  border-radius: 0;
  box-shadow: 0;
  background-color: #505050;
  position: relative;
}

/* Opacity slider : IE */

.GPlayerOpacity input[type="range"]::-ms-track {
  border: 0;
  border-color: transparent;
  border-radius: 0;
  border-width: 0;
  color: transparent;
  height: 3px;
  width: 80px;
}

.GPlayerOpacity input[type="range"]::-ms-fill-lower,
.GPlayerOpacity input[type="range"]::-ms-fill-upper {
  background: transparent;
  border-radius: 0;
}

.GPlayerOpacity input[type="range"]::-ms-thumb {
  width: 5px;
  height: 13px;
  border: 2px solid #FFF;
  background-color: #505050;
}

.GPlayerOpacity input[type="range"]::-ms-tooltip {
  display: none;
}

/* Layer info panel */

div[id^=GPlayerInfoPanel] {
  position: absolute;
  top: 0;
  overflow-y: hidden;
  padding-top: 10px;
  padding-bottom: 10px;
}

.GPlayerInfoPanelOpened {
  display: block;
}

.GPlayerInfoPanelClosed {
  display: none;
}

div[id^=GPlayerInfoContent] {
  position: relative;
  width: 280px;
  max-height: 200px;
  overflow-y: auto;
  padding-left: 10px;
  padding-right: 10px;
}

div[id^=GPlayerInfoTitle] {
  width: calc(100% - 52px);
  margin: auto;
  margin-bottom: 10px;
  padding-bottom: 5px;
  text-align: center;
  font-size: 1.1em;
  font-weight: bold;
}

div[id^=GPlayerInfoQuicklook] {
  position: absolute;
  top: -2px;
  left: 10px;
  width: 20px;
  height: 20px;
  cursor: pointer;
}

div[id^=GPlayerInfoClose] {
  position: absolute;
  top: -8px;
  right: 10px;
  width: 28px;
  height: 28px;
  cursor: pointer;
}

div[id^=GPlayerInfoDescription] {
  font-size: 0.9em;
}

div[id^=GPlayerInfoLegend] {

}

div[id^=GPlayerInfoMetadata] {

}

.GPlayerInfoSubtitle {
  padding-left: 35px;
  font-weight: bold;
  margin-top: 10px;
  margin-bottom: 4px;
}

.GPlayerInfoLink,
.GPlayerInfoPopup {
  line-height: 20px;
  padding-left: 23px;
  white-space: nowrap;
  overflow-x: hidden;
  text-overflow: ellipsis;
  color: #999999;
  background-repeat: no-repeat;
  cursor: pointer;
}

.GPlayerInfoLink {
  background-position: 0 -20px;
}

.GPlayerInfoLink:hover,
.GPlayerInfoPopup:hover {
  color: #333;
}

.GPlayerInfoLink a,
.GPlayerInfoLink a:visited
.GPlayerInfoLink a:focus
.GPlayerInfoLink a:hover {
  text-decoration: none;
  color: inherit;
}
