  display: inline-block;
}

input[id^=GPshowRoute-] + label {
  display: block;
}

input[id^=GPshowRoute-]:checked + label {
  display: none;
}

input[id^=GPshowRoute-] + label + div {
  display: none;
}

input[id^=GPshowRoute-]:checked + label + div {
  display: block;
}

/* General panels */

div[id^=GProutePanel-] {
  width: 320px;
}

.GProuteComponentHidden {
  display: none;
}

