}

.GProuteStageRemove {
  background-position: -84px 0;
}

div[id^=GProuteStageAdd] {
  background-position: -56px 0;
}

/* Mode choice */

div[id^=GProuteModeChoice] {
  display: -webkit-flex;
  display: -ms-flexbox;
  display: -webkit-box;
  display: flex;
  -webkit-justify-content: space-around;
  -ms-flex-pack: distribute;
      justify-content: space-around;
