/* Calc waiting */

.GPelevationPathCalcWaitingContainerVisible,
.GPelevationPathCalcWaitingContainerHidden {
  position: absolute;
  top: 32px;
  bottom: 0;
  left: 0;
  right: 0;
  background-color: rgba(255,255,255,0.9);
  font-size: 1.5em;
  font-weight: bold;
  overflow: hidden;
}

.GPelevationPathInformationsContainerVisible,
.GPelevationPathInformationsContainerHidden {
  position: absolute;
  top: 32px;
  bottom: 0;
  left: 0;
  right: 0;
  background-color: rgba(255,255,255,0.9);
  font-size: 13px;
  font-weight: bold;
  overflow: hidden;
}

.GPelevationPathCalcWaitingContainerVisible {
  display: -webkit-flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}

.GPelevationPathInformationsContainerVisible {
  display: -webkit-flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  flex-direction: column;
}

.GPelevationPathCalcWaitingContainerHidden,
.GPelevationPathInformationsContainerHidden {
  display: none;
}

.GPelevationPathCalcWaiting,
.GPelevationPathInformations {
  margin: auto;
}

.GPelevationPathInformations {
  font-size: 0.8em;
}

.GPelevationPathCalcWaitingContainerVisible,
.GPelevationPathCalcWaitingContainerHidden,
.GPelevationPathInformationsContainerVisible,
.GPelevationPathInformationsContainerHidden {
  border-radius: 5px;
}

