/* CSS : D3 */

.axis-d3 > text {
  fill: rgb(94, 94, 94);
  font-family: Verdana;
  font-size: 10px;
  opacity: 1;
  text-anchor: end;
}

.axis-d3 {
  fill: none;
  stroke: #5E5E5E;
  stroke-width: 1;
  shape-rendering: crispEdges;
}

.area-d3 {
  fill: #C77A04;
  stroke: #5E5E5E;
  stroke-width: 0;
  fill-opacity: 0.4;
}

.line-d3 {
  fill: none;
  stroke: #C77A04;
  stroke-width: 1px;
}

.grid-d3 .tick {
  stroke: lightgrey;
  opacity: 0.7;
}

.grid-d3 path {
  stroke-width: 0;
}

.grid-d3 .tick {
  stroke: lightgrey;
  opacity: 0.7;
}

.grid-d3 path {
  stroke-width: 0;
}

.overlay-d3 {
  fill: none;
  pointer-events: all;
}

.focusLine-d3 {
  fill: none;
  stroke: #C77A04;
  stroke-width: 0.5px;
}

.focusCircle-d3 {
  fill: #C77A04;
}

div.tooltip-d3 {
